                                $G{$xx+$ff2_LF+$extra} = $count;
                            }
                            $xx++;
                        }
                    }
                }
            }
        }
        $ff2_LF++;
    }
    my $pos_build = $SNP_pos-25;
    while ($pos_build < $SNP_pos+25)
    {
        my $A = $A{$pos_build};
        my $C = $C{$pos_build};
        my $T = $T{$pos_build};
        my $G = $G{$pos_build};
        print OUTPUT5 $pos_build." POS_BUILD\n";
        print OUTPUT5 $A." A\n";
        print OUTPUT5 $C." C\n";
        print OUTPUT5 $T." T\n";
        print OUTPUT5 $G." G\n";
        my @IUPAC = IUPAC($A,$C,$T,$G);
        $new_next_seq .= $IUPAC[0];
        $pos_build++;
    }
    $new_SNP_pos = '26';
    return ($new_next_seq, $new_SNP_pos);
}

